const obj = {
  name: 'Nandini',
  age: 24
}
const userReducer = (state, action) => {
  switch (action.type) {
    case 'SET_NAME' :
      return { ...state, name: action.payload }
    case 'SET_AGE' :
      return { ...state, age: action.payload }
    default: console.log('Nothing')
  }
}

const store = createStore(userReducer, obj)

store.subscribe(() => {
  console.log('Store updated', store.getState())
})

store.dispatch({
  type: 'SET_AGE',
  payload: 11
})
