import React from 'react'

class App extends React.Component {
  constructor () {
    super()
    this.state = {
      toggle: true
    }
  }

  handleToggle () {
    this.setState((state) => 
      ({ state: !this.state.toggle })
    )
  }

  render () {
    return (
      <>
        <div>Toggle {this.state.toggle ? 'On' : 'Off'} </div>
        <button onClick={() => this.handleToggle()}>
          {this.state.toggle ? 'On' : 'Off'}
        </button>
      </>
    )
  }
}

export default App
