import React from 'react'
import ReactDOM from 'react-dom'
import './index.css'
import App from './App'

import { createStore, combineReducers } from 'redux'

// Multiple reducers

const mathReducer = (state = {
  val: 100,
  array: []
}, action) => {
  switch (action.type) {
    case 'ADD' :
      state = { ...state, val: state.val + action.payload }
      break
    case 'SUB' :
      state = { ...state, array: [...state.array, action.payload] }
  }
  return state
}

const userReducer = (state = {
  name: 'Nandini',
  age: 24
}, action) => {
  switch (action.type) {
    case 'SET_NAME' :
      state = { ...state, name: action.payload }
      break
    case 'SET_AGE' :
      state = { ...state, age: action.payload }
  }
  return state
}

const store = createStore(combineReducers({ user: userReducer, maths: mathReducer }))

store.subscribe(() => {
  console.log('Store updated', store.getState())
})

store.dispatch({
  type: 'SET_AGE',
  payload: 11
})
store.dispatch({
  type: 'ADD',
  payload: 11
})

