import React, { Component } from 'react'

class Welcome extends Component {
  constructor (props) {
    super(props)
    this.state = {
      count: 0
    }
  }

  handleState () {
    this.setState(function (state) {
      {count: state.count + 1}
    }
    )
  }

  render () {
    return (
      <>
        <h1>Hello {this.state.count}</h1>
        <button onClick={() => this.handleState()}>Change</button>
      </>
    )
  }
}

export default Welcome
