import React from 'react'

function Login (props) {
  function handleLogin () {
    props.onLogin(false)
  }
  return (
    <>
      <h1>Please Sign In</h1>
      <button onClick={handleLogin}>Login</button>
    </>
  )
}

function SignUp (props) {
  function handleSignUp () {
    props.onSignUp(true)
  }
  return (
    <>
      <h1>Welcome Back</h1>
      <button onClick={handleSignUp}>Sign Up</button>
    </>
  )
}

class App extends React.Component {
  constructor () {
    super()
    this.state = {
      toggle: true
    }
  }

  handleLogin (val) {
    this.setState({
      toggle: val
    })
  }

  handleSignUp (val) {
    this.setState({
      toggle: val
    })
  }

  render () {
    if (this.state.toggle) return <Login onLogin={(val) => this.handleLogin(val)} />
    return <SignUp onSignUp={(val) => this.handleSignUp(val)} />
  }
}

export default App
