import React from 'react'

class App extends React.Component {
  constructor () {
    super()
    this.state = {
      val: '',
      full: ''
    }
  }

  handleSubmit (e) {
    e.preventDefault()
    this.setState({
      full: this.state.val,
      val: ''
    })
  }

  handleChange (e) {
    this.setState({
      val: e.target.value
    })
  }

  render () {
    return (
      <form onSubmit={(e) => this.handleSubmit(e)}>
        <h1>{this.state.full}</h1>
        <select value={this.state.val}>
          <option>name</option>
          <option>place</option>
          <option>DOB</option>
          <option>Job</option>
        </select>
        <button>Submit</button>
      </form>
    )
  }
}

export default App
