import React from 'react'

class App extends React.Component {
  constructor (props) {
    super(props)
    this.state = {
      date: new Date()
    }
  }

  tick () {
    this.setState({
      date: new Date()
    })
  }

  componentDidMount () {
    this.id = setInterval(() => this.tick(), 1000)
  }

componentWillUnmount () {
  clearInterval(this.id)
}

  render () {
    return (
      <div>
        <h1>Hello World</h1>
        <h2>It is {this.state.date.toLocaleTimeString()}.</h2>
      </div>
    )
  }
}

export default App
